void browse(pthread_attr_t at1,pthread_attr_t at2)
{
	int numCPU = sysconf( _SC_NPROCESSORS_ONLN );
	int pol1,pol2;
	pthread_attr_getschedpolicy(&at1, &pol1);
	pthread_attr_getschedpolicy(&at1, &pol2);

	std::cout << "Anzahl der Kerne: " << numCPU << std::endl;
	 std::cout << "Thread 1: " ;
	 switch(pol1){
	 case SCHED_FIFO:
		 std::cout << "FIFO. " << std::endl;
		 break;
	 case SCHED_RR:
		 std::cout << "RoundRobin. " << std::endl;
		 break;
	 case SCHED_OTHER:
		 std::cout << "Other. " << std::endl;
		 break;
	 }

	 std::cout << "Thread 2: " ;
	 switch(pol2){
	 case SCHED_FIFO:
		 std::cout << "FIFO. " << std::endl;
		 break;
	 case SCHED_RR:
		 std::cout << "RoundRobin. " << std::endl;
		 break;
	 case SCHED_OTHER:
		 std::cout << "Other. " << std::endl;
		 break;
	 }

}

